CREATE PROCEDURE [dbo].[spr_list_Stock_ledger_by_brand_Top](@BRAND nvarchar(2550), @FROM_DATE datetime,     @ShowItems nvarchar(50), @ItemCode nvarchar(2550))     AS          Declare @Delimeter as Char(1)         Set @Delimeter=Char(15)         Create table #tmpDiv(Division nvarchar(255) COLLATE SQL_Latin1_General_CP1_CI_AS)          create table #tmpProd(product_code nvarchar(255) COLLATE SQL_Latin1_General_CP1_CI_AS)     if @BRAND='%'            Insert into #tmpDiv select BrandName from Brand         Else            Insert into #tmpDiv select * from dbo.sp_SplitIn2Rows(@BRAND,@Delimeter)              if @ItemCode = '%'      Insert InTo #tmpProd Select Product_code From Items     Else      Insert into #tmpProd select * from dbo.sp_SplitIn2Rows(@ItemCode, @Delimeter)                   IF (DATEPART(dy, @FROM_DATE) < DATEPART(dy, GETDATE()) AND DATEPART(yyyy, @FROM_DATE) = DATEPART(yyyy, GETDATE())) OR DATEPART(yyyy, @FROM_DATE) < DATEPART(yyyy, GETDATE())              BEGIN                IF @ShowItems = 'Items with stock'                BEGIN                    Select  Items.BrandID,                        "Division" = Brand.BrandName,                                      "Saleable Stock" = sum(isnull(openingdetails.Opening_Quantity,0) - isnull(openingdetails.Free_Saleable_Quantity,0) - isnull(openingdetails.Damage_Opening_Quantity,0)),                     "Free OnHand Qty" = isnull(sum(openingdetails.Free_Saleable_Quantity ), 0),                       "Damages Qty" = isnull(sum(openingdetails.Damage_Opening_Quantity),0),       "Total On Hand Qty" = ISNULL(SUM(OpeningDetails.Opening_Quantity), 0)                      from    Items, OpeningDetails, Brand                       WHERE   Items.Product_Code *= OpeningDetails.Product_Code                        AND OpeningDetails.Opening_Date = DATEADD(d, 1, @FROM_DATE)                       AND Items.BrandID = Brand.BrandID                        AND Brand.BrandName In (Select Division COLLATE SQL_Latin1_General_CP1_CI_AS from #tmpDiv)         AND Items.Product_Code in (Select product_code COLLATE SQL_Latin1_General_CP1_CI_AS from #tmpProd)         and Brand.active  = 1                  GROUP BY Items.BrandID, Brand.BrandName                       HAVING ISNULL(SUM(OpeningDetails.Opening_Quantity), 0) > 0                 END                    ELSE                BEGIN                  Select  Items.BrandID,                        "Division" = Brand.BrandName,                        "Saleable Stock" = sum(isnull(openingdetails.Opening_Quantity,0) - isnull(openingdetails.Free_Saleable_Quantity,0) - isnull(openingdetails.Damage_Opening_Quantity,0)),                     "Free OnHand Qty" = isnull(sum(openingdetails.Free_Saleable_Quantity ), 0),                       "Damages Qty" = isnull(sum(openingdetails.Damage_Opening_Quantity),0),       "Total On Hand Qty" = ISNULL(SUM(OpeningDetails.Opening_Quantity), 0)                    from    Items, OpeningDetails, Brand                       WHERE   Items.Product_Code *= OpeningDetails.Product_Code                       AND OpeningDetails.Opening_Date = DATEADD(d, 1, @FROM_DATE)                       AND Items.BrandID = Brand.BrandID                        AND Brand.BrandName In (Select Division COLLATE SQL_Latin1_General_CP1_CI_AS from #tmpDiv)                        AND Items.Product_Code in (Select product_code COLLATE SQL_Latin1_General_CP1_CI_AS from #tmpProd)         and Brand.active  = 1                  GROUP BY Items.BrandID, Brand.BrandName                     END               END               ELSE                    BEGIN                    IF @ShowItems = 'Items with stock'                BEGIN                  Select  Items.BrandID,                        "Division" = b1.BrandName,           "Saleable Stock" = isnull((select isnull(Sum(Quantity),0) from batch_products, Items where Items.Product_Code = Batch_Products.Product_Code and isnull(free,0)=0 and isnull(damage,0) = 0 And Items.BrandID = b1.BrandID),0),                   "Free OnHand Qty" = isnull((select isnull(sum(Quantity),0) from Batch_Products, Items where Items.Product_Code = Batch_Products.Product_Code and free <> 0 And IsNull(Damage, 0) <> 1 And Items.BrandID = b1.BrandID),0),                     "Damages Qty" = isnull((select isnull(sum(Quantity),0) from Batch_Products, items where Items.Product_Code = Batch_Products.Product_Code and isnull(damage,0) <> 0 And Items.BrandID = b1.BrandID),0),                     "Total On Hand Qty" = ISNULL(SUM(QUANTITY), 0)                     from  Items, Batch_Products, Brand b1, ItemCategories IC                      WHERE  Items.BrandID = b1.BrandID                        AND Items.Product_Code *= Batch_Products.Product_Code              AND Items.CategoryID = IC.CategoryID                   AND b1.BrandName In (Select Division COLLATE SQL_Latin1_General_CP1_CI_AS from #tmpDiv)          AND Items.Product_Code in (Select product_code COLLATE SQL_Latin1_General_CP1_CI_AS from #tmpProd)         and b1.active  = 1                  GROUP BY Items.BrandID, b1.BrandID, b1.BrandName                      HAVING ISNULL(SUM(QUANTITY), 0) > 0                END                ELSE              BEGIN                  Select  Items.BrandID,                        "Division" = b1.BrandName,                       "Saleable Stock" = isnull((select isnull(Sum(Quantity),0) from batch_products, Items where Items.Product_Code = Batch_Products.Product_Code and isnull(free,0)=0 and isnull(damage,0) = 0 And Items.BrandID = b1.BrandID),0),                   "Free OnHand Qty" = isnull((select isnull(sum(Quantity),0) from Batch_Products, Items where Items.Product_Code = Batch_Products.Product_Code and free <> 0 And IsNull(Damage, 0) <> 1 And Items.BrandID = b1.BrandID),0),                     "Damages Qty" = isnull((select isnull(sum(Quantity),0) from Batch_Products, items where Items.Product_Code = Batch_Products.Product_Code and isnull(damage,0) <> 0 And Items.BrandID = b1.BrandID),0),         "Total On Hand Qty" = ISNULL(SUM(QUANTITY), 0)                         from  Items, Batch_Products, Brand b1, ItemCategories IC                      WHERE  Items.BrandID = b1.BrandID                        AND Items.Product_Code *= Batch_Products.Product_Code                       AND Items.CategoryID = IC.CategoryID           AND b1.BrandName In (Select Division COLLATE SQL_Latin1_General_CP1_CI_AS from #tmpDiv)          AND Items.Product_Code in (Select product_code COLLATE SQL_Latin1_General_CP1_CI_AS from #tmpProd)         and b1.active  = 1                  GROUP BY Items.BrandID, b1.BrandID, b1.BrandName                     END                  END               Drop table #tmpDiv
